// sha512验证
func Sha512Str(src string) string {
	h := sha512.New()
	h.Write([]byte(src))
	return hex.EncodeToString(h.Sum(nil))
}

type UserInfo struct {
	UserId int    `json:"user_id"`
	Salt   string `json:"salt"`
	Passwd string `json:"passwd"`
}

